import React, {useState} from 'react';
import { Card, CardImg, Button, Form, FormGroup, Label, Input, Dropdown, DropdownToggle, DropdownMenu } from 'reactstrap';

const OrderForm = props => {
const [dropdownOpen, setDropdownOpen] = useState(false);
const toggle = () => setDropdownOpen((prevState)=> !prevState)
const [formData, setFormData] = useState({
    name: "",
    number: 0,
    cheese: false,
    tomatoes: false,
    olives: false,
    lettuce: false,
    hotSauce: false,
    sourCream: false,
    special: ""
})
const handleChanges = event => {

    setFormData({ ...formData, [event.target.name]: event.target.value });  
  };
const handleToppings = event => {
    setFormData({...formData, [event.target.name]: event.target.checked })
}

  const submitForm = event => {
    event.preventDefault();
    setFormData({name: "",
    number: 0,
    cheese: false,
    tomatoes: false,
    olives: false,
    lettuce: false,
    hotSauce: false,
    sourCream: false,
    special: ""})
  }


    return (
        <>
        <Card color="info">
            <h2 style={{color: "white", margin: "0 auto"}}>Build Your Own Taco</h2>
         <CardImg style={{width: '80%', margin: "0 auto"}} src={require('../assets/taco-2.jpg')} />  
        </Card>
        <Form style={{margin: "5%"}}>
            <FormGroup>
                <Label for="name">Name</Label>
                <Input name="name" id="name" placeholder="name" value={formData.name} onChange={handleChanges}/>
            </FormGroup>
            <FormGroup>
        <Dropdown isOpen={dropdownOpen} toggle={toggle}>
        <DropdownToggle caret>{formData.number === 0 ? "Quantity" : formData.number}
  </DropdownToggle>
  <DropdownMenu>
  <option onClick={() => {
            toggle();
            setFormData({...formData, number: 0})
        }}>0</option>
        <option onClick={() => {
            toggle();
            setFormData({...formData, number: 1})
        }}>1</option>
          <option onClick={() => {
            toggle();
            setFormData({...formData, number: 2})
        }}>2</option>
          <option onClick={() => {
            toggle();
            setFormData({...formData, number: 3})
        }}>3</option>
          <option onClick={() => {
            toggle();
            setFormData({...formData, number: 4})
        }}>4</option>
          <option onClick={() => {
            toggle();
            setFormData({...formData, number: 5})
        }}>5</option>
          <option onClick={() => {
            toggle();
            setFormData({...formData, number: 6})
        }}>6</option>
          </DropdownMenu>
        </Dropdown>
          
      </FormGroup>
      <FormGroup check>
        <Label check>
          <Input type="checkbox" id="cheese" value="cheese" onChange={handleChanges}/>{' '}
          Cheese
        </Label>
      </FormGroup> 
      <FormGroup check>
        <Label check>
          <Input type="checkbox" id="tomatoes" value="cheese" onChange={handleChanges}/>{' '}
          Tomatoes
        </Label>
      </FormGroup> 
      <FormGroup check>
        <Label check>
          <Input type="checkbox" id="blackOlives"/>{' '}
          Black Olives
        </Label>
      </FormGroup> 
      <FormGroup check>
        <Label check>
          <Input type="checkbox" id="lettuce"/>{' '}
          Lettuce
        </Label>
      </FormGroup> 
      <FormGroup check>
        <Label check>
          <Input type="checkbox" id="hotSauce"/>{' '}
          Hot sauce
        </Label>
      </FormGroup> 
      <FormGroup check>
        <Label check>
          <Input type="checkbox" id="sourCream"/>{' '}
           Sour cream
        </Label>
      </FormGroup>
      <FormGroup>
        <Label for="special">Special Instructions</Label>
        <Input type="textarea" name="text" id="special" value={formData.special} onChange={handleChanges}/>
      </FormGroup>
      <Button>Submit</Button>
    </Form>
    </>
    )
}

export default OrderForm;
